CREATE OR REPLACE FUNCTION obter_se_elsevier_protocolo ( cd_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE


countProtocols bigint;


BEGIN

select count(nr_seq_tipo_prot)
into STRICT countProtocols
from  protocolo_guia_inf
where nr_seq_tipo_prot = cd_protocolo_p
and IE_TIPO = 'P';

if countProtocols > 0 then
