CREATE OR REPLACE FUNCTION obter_se_empresa_reenserida ( nr_seq_fornec_p bigint, nr_Seq_lic_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
qt_registro_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	reg_lic_fornec_lance
where	nr_seq_fornec = nr_seq_fornec_p
and	nr_seq_lic_item = nr_seq_lic_item_p
and	coalesce(ie_reenserida,'N') = 'S';

