CREATE OR REPLACE FUNCTION obter_se_em_agendamento ( dt_inicio_agendamento_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_tempo_em_agenda_w 	bigint;
ie_em_agendamento_w		varchar(2) := 'S';


BEGIN
begin
	select	max(qt_tempo_em_agenda)
	into STRICT 	qt_tempo_em_agenda_w
	from	parametro_agenda
	where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

	if (dt_inicio_agendamento_p IS NOT NULL AND dt_inicio_agendamento_p::text <> '') and (coalesce(qt_tempo_em_agenda_w, 0) > 0) then

		if (PKG_DATE_UTILS.get_DiffDate(dt_inicio_agendamento_p,clock_timestamp(),'MINUTE') > qt_tempo_em_agenda_w) then
			ie_em_agendamento_w := 'N';
		end if;

	end if;
exception
	when others then
		ie_em_agendamento_w := 'N';
end;
return	ie_em_agendamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
