SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_em_protocolo_vanco ( nr_atendimento_p bigint, ie_retorno_padrao_p text, qt_dias_reinicio_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1)	:= '';
dt_fim_protocolo_w		timestamp;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	max(dt_atualizacao_nrec)
	into STRICT	dt_fim_protocolo_w
	from	rep_dialogo_vancomicina
	where	ie_situacao = 'A'
	and		ie_tipo_evento = 'F'
	and		nr_atendimento = nr_atendimento_p;

	if (dt_fim_protocolo_w IS NOT NULL AND dt_fim_protocolo_w::text <> '') then
		select	coalesce(max('S'), 'N')
		into STRICT	ie_retorno_w
		from	rep_dialogo_vancomicina
		where	ie_situacao = 'A'
		and		ie_tipo_evento = 'I'
		and		dt_atualizacao_nrec > dt_fim_protocolo_w
		and		nr_atendimento = nr_atendimento_p;
		
		if (ie_retorno_w = 'N') and (qt_dias_reinicio_p > 0) and (clock_timestamp() >= establishment_timezone_utils.startofday(dt_fim_protocolo_w+qt_dias_reinicio_p)) then
			ie_retorno_w	:= 'Q';
		end if;
	else
		select	max('S')
		into STRICT	ie_retorno_w
		from	rep_dialogo_vancomicina
		where	ie_situacao = 'A'
		and		ie_tipo_evento = 'I'
		and		nr_atendimento = nr_atendimento_p;			
	end if;
end if;

return	coalesce(ie_retorno_w, ie_retorno_padrao_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
