CREATE OR REPLACE FUNCTION obter_se_entrega_biometrica (nr_prescricao_p bigint, nr_seq_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_entrega_biometrica_w    varchar(1);
ie_entrega_w               varchar(1);


BEGIN

select CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT ie_entrega_w
from prescr_proc_info_laudo info
where info.nr_prescricao = nr_prescricao_p
and   info.nr_seq_prescr = nr_seq_prescricao_p;

if (ie_entrega_w = 'S') then
   select CASE coalesce(WHEN(dt_biometria)::text, '') = '' THEN 'N'  ELSE 'S' END
   into STRICT ie_entrega_biometrica_w
   from (
        SELECT nr_sequencia, dt_biometria
        from prescr_proc_info_laudo
        where nr_prescricao = nr_prescricao_p
        and   nr_seq_prescr = nr_seq_prescricao_p
        order by nr_sequencia) alias3 LIMIT 1;
end if;

return  ie_entrega_biometrica_w;

end;
$body$
