CREATE OR REPLACE FUNCTION obter_se_entre_horario ( dt_referencia_p timestamp, hr_inicio_p timestamp, hr_final_p timestamp) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(10)	:= 'N';

dt_inicio_w	timestamp;
dt_final_w	timestamp;


BEGIN

dt_inicio_w	:= to_date(to_char(dt_referencia_p,'dd/mm/yyyy')||' '|| to_char(hr_inicio_p,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss');

dt_final_w	:= to_date(to_char(dt_referencia_p,'dd/mm/yyyy')||' '|| to_char(hr_final_p,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss');

if (dt_final_w	< dt_inicio_w) then
	dt_final_w	:= dt_final_w + 1;
end if;


if (dt_referencia_p	>= dt_inicio_w) and (dt_referencia_p	<= dt_final_w) then
	ds_retorno_w	:= 'S';
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
