SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_ent_cot_menor_ret ( nr_cot_compra_p bigint) RETURNS varchar AS $body$
DECLARE


/*Essa function é usada no momento de enviar a cotação para a bionexo.
Ela consiste caso possui alguma entrega com data menor que a dara retorno previsto*/
dt_retorno_prev_w				timestamp;
ie_retorno_w				varchar(1) := 'N';
qt_existe_w				bigint;


BEGIN

select	trunc(dt_retorno_prev,'dd')
into STRICT	dt_retorno_prev_w
from	cot_compra
where	nr_cot_compra = nr_cot_compra_p;

select	count(*)
into STRICT	qt_existe_w
from	cot_compra_item_entrega
where	nr_cot_compra = nr_cot_compra_p
and	trunc(dt_entrega,'dd') < dt_retorno_prev_w;

if (qt_existe_w > 0) then
	ie_retorno_w := 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_ent_cot_menor_ret ( nr_cot_compra_p bigint) FROM PUBLIC;

