CREATE OR REPLACE FUNCTION obter_se_envia_cancel_bionexo ( nr_cot_compra_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w			varchar(1);
ds_url_w				varchar(255);
ds_login_w			varchar(255);
ds_senha_w			varchar(255);
nr_doc_externo_w			varchar(10);
cd_estabelecimento_w		integer;


BEGIN 
 
select	max(nr_documento_externo), 
	max(cd_estabelecimento) 
into STRICT	nr_doc_externo_w, 
	cd_estabelecimento_w 
from	cot_compra 
where	nr_cot_compra = nr_cot_compra_p;
 
select	ds_url_compras, 
	ds_login_integr_compras_ws, 
	ds_senha_integr_compras_ws 
into STRICT	ds_url_w, 
	ds_login_w, 
	ds_senha_w 
from	parametro_compras 
where	cd_estabelecimento = cd_estabelecimento_w;
 
if (coalesce(nr_doc_externo_w::text, '') = '') or (coalesce(ds_url_w::text, '') = '') or (coalesce(ds_login_w::text, '') = '') or (coalesce(ds_senha_w::text, '') = '') then 
	ie_retorno_w	:= 'N';
else 
	ie_retorno_w	:= 'S';
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
