CREATE OR REPLACE FUNCTION obter_se_envia_ci_regra_compra ( nr_seq_regra_p bigint, nr_documento_p bigint, ie_tipo_documento_p text, cd_perfil_p bigint, nm_usuario_p text, cd_centro_custo_p bigint) RETURNS varchar AS $body$
DECLARE


/*Tipo de Documento
OC - Ordem de Compra
SC - Solicitação de Compra
CC - Cotação de Compra
AF - Avaliação Fornecedor
NF - Nota Fiscal
IR - Inspeção Recebimento
RM - Requisão de materiais
LF - Lote Fornecedor
MP - MATERIAL_PADRONIZACAO
*/
ie_retorno_w			varchar(1)	:= 'S';
qt_existe_w			bigint;

c01 CURSOR FOR
SELECT	coalesce(ie_envia_comunic,'S')
from	regra_envio_comunic_filtro
where	nr_seq_regra = nr_seq_regra_p
and (coalesce(cd_perfil_regra,cd_perfil_p)			= cd_perfil_p)
and (coalesce(nm_usuario_regra,nm_usuario_p)			= nm_usuario_p)
and (coalesce(cd_centro_custo,coalesce(cd_centro_custo_p,'0'))	= coalesce(cd_centro_custo_p,'0'))
order by	coalesce(cd_perfil_regra,0),
	coalesce(nm_usuario_regra,'0');


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	regra_envio_comunic_filtro
where	nr_seq_regra = nr_seq_regra_p;

if (qt_existe_w > 0) then
	begin

	open c01;
	loop
	fetch c01 into
		ie_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close c01;

	end;
else
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
