CREATE OR REPLACE FUNCTION obter_se_envia_email_internet ( ie_tipo_mensagem_p bigint, nr_seq_fornec_p bigint, cd_estabelecimento_p bigint, nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_envia_email_w			varchar(1) := 'S';			
ie_somente_lib_internet_w		varchar(1);
ie_liberada_internet_w			varchar(1);
cd_cgc_fornec_w				varchar(14);
nr_seq_fornec_w				bigint;
nr_cot_compra_w				bigint;


BEGIN 
 
select	coalesce(max(ie_somente_lib_internet),'N') 
into STRICT	ie_somente_lib_internet_w 
from	regra_envio_email_compra 
where	ie_tipo_mensagem	= ie_tipo_mensagem_p 
and	cd_estabelecimento	= cd_estabelecimento_p 
and	ie_situacao		= 'A';
 
if (ie_somente_lib_internet_w = 'S') and (nr_seq_fornec_p IS NOT NULL AND nr_seq_fornec_p::text <> '')then 
 
	select	coalesce(max(ie_liberada_internet),'N') 
	into STRICT	ie_liberada_internet_w 
	from	cot_compra_forn 
	where	nr_sequencia	= nr_seq_fornec_p;
	 
	if (ie_liberada_internet_w = 'N') then 
		ie_envia_email_w		:= 'N';	
	end if;
else 	 
	select	coalesce(max(a.cd_cgc_fornecedor),0), 
		coalesce(max(b.nr_cot_compra),0) 
	into STRICT	cd_cgc_fornec_w, 
		nr_cot_compra_w 
	from	ordem_compra a, 
		ordem_compra_item b 
	where	a.nr_ordem_compra = b.nr_ordem_compra 
	and	a.nr_ordem_compra = nr_ordem_compra_p;
	 
	if (cd_cgc_fornec_w > 0) and (nr_cot_compra_w > 0) then 
		begin 
		select	coalesce(max(nr_sequencia),0) 
		into STRICT	nr_seq_fornec_w 
		from	cot_compra_forn 
		where	cd_cgc_fornecedor = cd_cgc_fornec_w 
		and	nr_cot_compra = nr_cot_compra_w;
		 
		if (nr_seq_fornec_w > 0) then 
		 
			select	coalesce(max(ie_liberada_internet),'N') 
			into STRICT	ie_liberada_internet_w 
			from	cot_compra_forn 
			where	nr_sequencia	= nr_seq_fornec_w;
		 
			if (ie_liberada_internet_w = 'N') and (ie_somente_lib_internet_w = 'S') then 
				ie_envia_email_w		:= 'N';	
			end if;
		end if;
		end;
	end if;	
	 
end if;
 
return	ie_envia_email_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
