CREATE OR REPLACE FUNCTION obter_se_envia_email_ordem ( ie_tipo_mensagem_p bigint, nr_ordem_compra_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_envia_email_w			varchar(1) := 'S';			
ie_somente_lib_internet_w		varchar(1);
ie_liberada_internet_w		varchar(1);
nr_cot_compra_w			bigint;
nr_seq_fornec_w			bigint;
cd_cgc_w			varchar(14);


BEGIN 
 
select	coalesce(max(cd_cgc_fornecedor),0) 
into STRICT	cd_cgc_w 
from	ordem_compra 
where	nr_ordem_compra = nr_ordem_compra_p;
 
select 	coalesce(max(nr_cot_compra),0) 
into STRICT	nr_cot_compra_w 
from 	ordem_compra_item 
where	nr_ordem_compra = nr_ordem_compra_p;
 
select	coalesce(max(ie_liberada_internet),'N') 
into STRICT	ie_liberada_internet_w 
from	cot_compra_forn 
where	nr_cot_compra 		= nr_cot_compra_w 
and		cd_cgc_fornecedor	= cd_cgc_w;
 
select	coalesce(max(ie_somente_lib_internet),'N') 
into STRICT	ie_somente_lib_internet_w 
from	regra_envio_email_compra 
where	ie_tipo_mensagem	= ie_tipo_mensagem_p 
and		cd_estabelecimento	= cd_estabelecimento_p 
and		ie_situacao			= 'A';
 
if (ie_somente_lib_internet_w = 'S') then 
 
	select	coalesce(max(ie_liberada_internet),'N') 
	into STRICT	ie_liberada_internet_w 
	from	cot_compra_forn 
	where	cd_cgc_fornecedor	= cd_cgc_w 
	and		nr_cot_compra 		= nr_cot_compra_w;
	 
	if (ie_liberada_internet_w = 'N') then 
		ie_envia_email_w		:= 'N';	
	end if;
end if;
 
return	ie_envia_email_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
