SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_episodio_cid ( cd_doenca_cid_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
nr_seq_episodio_w	episodio_paciente.nr_sequencia%type;


BEGIN

if (cd_doenca_cid_p IS NOT NULL AND cd_doenca_cid_p::text <> '') and (coalesce(nr_atendimento_p,0) > 0) and (obter_uso_case(wheb_usuario_pck.get_nm_usuario) = 'S')	then

	select	nr_seq_episodio
	into STRICT	nr_seq_episodio_w
	from	atendimento_paciente
	where	nr_atendimento = nr_atendimento_p;

	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	diagnostico_doenca
	where	nr_atendimento		in (	SELECT 	a.nr_atendimento
										from 	atendimento_paciente a,
												episodio_paciente b
										where 	a.nr_seq_episodio = b.nr_sequencia
										and 	b.nr_sequencia = nr_seq_episodio_w)
	and		cd_doenca			= cd_doenca_cid_p
	and		coalesce(ie_situacao,'A') = 'A';

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
