CREATE OR REPLACE FUNCTION obter_se_escala_agenda (nr_seq_grupo_escala_p bigint, cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_tipo_agenda_w bigint;
qt_regra_w bigint;
ds_retorno_w varchar(1);


BEGIN 
 
ds_retorno_w := 'S';
 
cd_tipo_agenda_w := obter_tipo_agenda(cd_agenda_p);
 
select count(*) 
into STRICT qt_regra_w 
from escala_grupo_agenda 
where nr_seq_grupo = nr_seq_grupo_escala_p;
 
if (qt_regra_w > 0) then 
 
	select CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT ds_retorno_w 
	from escala_grupo_agenda 
	where nr_seq_grupo = nr_seq_grupo_escala_p 
	and cd_tipo_agenda = cd_tipo_agenda_w;
 
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
