CREATE OR REPLACE FUNCTION obter_se_escala_lib_aplicacao ( ie_escala_p bigint, ie_aplicacao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(1) := 'N';
cd_pais_w		valor_dominio_pais.cd_pais%type;
qt_registro_w	bigint;
					

BEGIN 
 
 
if ( ie_aplicacao_p = 'H') then 
 
	Select coalesce(max('S'),'N') 
	into STRICT	ds_retorno_w 
	from	escala_documentacao 
	where	ie_escala = ie_escala_p 
	and		ie_html = 'S';
	 
elsif (ie_aplicacao_p = 'D') then 
 
	Select coalesce(max('S'),'N') 
	into STRICT	ds_retorno_w 
	from	escala_documentacao 
	where	ie_escala = ie_escala_p 
	and		ie_delphi = 'S';
	 
	 
elsif (ie_aplicacao_p = 'J') then 
 
	Select coalesce(max('S'),'N') 
	into STRICT	ds_retorno_w 
	from	escala_documentacao 
	where	ie_escala = ie_escala_p 
	and		ie_java = 'S';
 
elsif (ie_aplicacao_p = 'DJ') then 
 
	Select coalesce(max('S'),'N') 
	into STRICT	ds_retorno_w 
	from	escala_documentacao 
	where	ie_escala = ie_escala_p 
	and		((ie_delphi = 'S') or (ie_java = 'S'));
	 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
