CREATE OR REPLACE FUNCTION obter_se_escala_lib_pais ( nr_seq_escala_p bigint, cd_pais_p bigint default null, ie_escala_p bigint default 0) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';
cd_pais_w		valor_dominio_pais.cd_pais%type;
qt_registro_w	bigint;
					

BEGIN

if (cd_pais_p IS NOT NULL AND cd_pais_p::text <> '') then

	cd_pais_w := cd_pais_p;	
	
else

	cd_pais_w := coalesce(coalesce(cd_pais_p,philips_param_pck.get_cd_pais), 0);
	
	if (cd_pais_w = 0) then
		cd_pais_w := 1;
	end if;

end if;



select	count(1)
into STRICT	qt_registro_w
from	escala_documentacao a,
	escala_documentacao_pais b
where	b.nr_seq_escala = a.nr_sequencia
and	((coalesce(nr_seq_escala_p,0) > 0 and a.nr_sequencia = nr_seq_escala_p)
or (coalesce(ie_escala_p,0) > 0 and a.ie_escala = ie_escala_p))
and	b.cd_pais = cd_pais_w;

if ( qt_registro_w > 0) then
	
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	escala_documentacao a,
		escala_documentacao_pais b
	where	b.nr_seq_escala = a.nr_sequencia
	and	((coalesce(nr_seq_escala_p,0) > 0 and a.nr_sequencia = nr_seq_escala_p)
	or (coalesce(ie_escala_p,0) > 0 and a.ie_escala = ie_escala_p))
	and	coalesce(b.ie_liberado,'S') = 'S'
	and	b.cd_pais = cd_pais_w;
end	if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
