CREATE OR REPLACE FUNCTION obter_se_especialidades_medico (cd_medico_p text, ds_lista_especialidade_p text) RETURNS varchar AS $body$
DECLARE


ie_esp_med_w	varchar(1);


BEGIN

if (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') and (ds_lista_especialidade_p IS NOT NULL AND ds_lista_especialidade_p::text <> '') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_esp_med_w
	from	medico_especialidade
	where	cd_pessoa_fisica	= cd_medico_p
	and	obter_se_contido_char(cd_especialidade,ds_lista_especialidade_p) = 'S';
end if;

return ie_esp_med_w;

end;
$body$
LANGUAGE PLPGSQL
