CREATE OR REPLACE FUNCTION obter_se_especialidade_medico ( cd_medico_p text, cd_especialidade_p bigint) RETURNS char AS $body$
DECLARE


ie_esp_med_w	char(1)	:= 'S';


BEGIN
if (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') and (cd_especialidade_p IS NOT NULL AND cd_especialidade_p::text <> '') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_esp_med_w
	from	medico_especialidade where		cd_pessoa_fisica	= cd_medico_p
	and		cd_especialidade	= cd_especialidade_p LIMIT 1;

end if;

return ie_esp_med_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
