CREATE OR REPLACE FUNCTION obter_se_espec_exame ( nr_seq_exame_p bigint, cd_medico_p text ) RETURNS varchar AS $body$
DECLARE

 
cd_especialidade_w	integer;
ie_utiliza_w		varchar(1) := 'N';

c01 CURSOR FOR 
	SELECT	cd_especialidade 
	from	medico_especialidade 
	where	cd_pessoa_fisica	=	cd_medico_p;


BEGIN 
 
OPEN C01;
LOOP 
FETCH C01 into 
	cd_especialidade_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	select	coalesce(max('S'),'N') 
	into STRICT	ie_utiliza_w 
	from	med_exame_padrao_espec 
	where	nr_seq_exame_padrao	=	nr_seq_exame_p 
	and	cd_especialidade	=	cd_especialidade_w;
 
	if (ie_utiliza_w = 'S') then 
		exit;
	end if;
 
	end;
END LOOP;
CLOSE C01;
 
if (ie_utiliza_w = 'N') then 
	select	coalesce(max('N'),'S') 
	into STRICT	ie_utiliza_w 
	from	med_exame_padrao_espec 
	where	nr_seq_exame_padrao	=	nr_seq_exame_p;
end if;
 
return	ie_utiliza_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
