CREATE OR REPLACE FUNCTION obter_se_espec_medico_plano ( cd_espec_plano_p bigint, cd_medico_exec_p text) RETURNS varchar AS $body$
DECLARE


ie_existe_w			varchar(5) := 'S';

BEGIN

if (coalesce(cd_espec_plano_p,0) <> 0) then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_existe_w
	from	especialidade_medica b,
		medico_especialidade a
	where	a.cd_especialidade	= b.cd_especialidade
	and	a.cd_pessoa_fisica	= cd_medico_exec_p
	and	b.cd_especialidade	= cd_espec_plano_p;
end if;


return ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
