CREATE OR REPLACE FUNCTION obter_se_espec_parecer ( cd_especialidade_p bigint) RETURNS varchar AS $body$
DECLARE


qt_espec_parecer_w	bigint;
cd_estabelecimento_w	bigint;
cd_estab_logado_w	bigint;
ds_retorno_w		varchar(1);


BEGIN
cd_estab_logado_w := Obter_Estabelecimento_Ativo;

select	count(*)
into STRICT	qt_espec_parecer_w
from	espec_medica_parecer
where	cd_especialidade = cd_especialidade_p;

if (qt_espec_parecer_w = 0) then
	ds_retorno_w := 'S';
elsif (qt_espec_parecer_w > 0) then
	select	count(*)
	into STRICT	qt_espec_parecer_w
	from	espec_medica_parecer
	where	cd_especialidade = cd_especialidade_p
	and		cd_estabelecimento = cd_estab_logado_w;

	if (qt_espec_parecer_w = 0) then
		ds_retorno_w := 'N';
	else
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
