CREATE OR REPLACE FUNCTION obter_se_espera_duplicada (NR_SEQ_AGEINT_ITEM_P bigint, CD_PESSOA_FISICA_P text default null, NM_PACIENTE_P text default null, NR_SEQ_PROC_INTERNO_P bigint default null, CD_ESPECIALIDADE_P bigint default null) RETURNS varchar AS $body$
DECLARE

 
 
ie_consiste_dupl_w	varchar(10);
cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;
nm_paciente_w		agenda_integrada.nm_paciente%type;
nr_seq_proc_interno_w	agenda_integrada_item.nr_seq_proc_interno%type;
cd_especialidade_w	agenda_integrada_item.cd_especialidade%type;
qt_reg_w		smallint := 0;
ds_retorno_w		varchar(255);
			

BEGIN 
 
ie_consiste_dupl_w  := Obter_param_usuario(869, 404, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_consiste_dupl_w );
 
if (ie_consiste_dupl_w <> 'N') then 
	if (coalesce(NR_SEQ_AGEINT_ITEM_P, 0) > 0) then 
		begin 
		select 	cd_pessoa_fisica, 
			nm_paciente,			 
			nr_seq_proc_interno,			 
			cd_especialidade 
		into STRICT	cd_pessoa_fisica_w, 
			nm_paciente_w,			 
			nr_seq_proc_interno_w, 
			cd_especialidade_w 
		from	agenda_integrada a, 
			agenda_integrada_item b 
		where	a.nr_sequencia = b.nr_seq_agenda_int 
		and	b.nr_sequencia = nr_seq_ageint_item_p;	
		exception 
		when no_data_found then 
			cd_pessoa_fisica_w := null;
		end;	
	ELSE 
		cd_pessoa_fisica_w := cd_pessoa_fisica_p;
		nm_paciente_w := nm_paciente_p;
		nr_seq_proc_interno_w := nr_seq_proc_interno_p;
		cd_especialidade_w := cd_especialidade_p;
	END IF;
	 
	IF ((coalesce(cd_pessoa_fisica_w, nm_paciente_w) IS NOT NULL AND (coalesce(cd_pessoa_fisica_w, nm_paciente_w))::text <> '')) THEN 
		IF (ie_consiste_dupl_w IN ('BPE', 'QPE')) THEN 
			 
			SELECT 	coalesce(MAX(1), 0) 
			INTO STRICT	qt_reg_w 
			FROM	AGENDA_LISTA_ESPERA a				 
			WHERE 	((a.cd_pessoa_fisica = cd_pessoa_fisica_w) or ((coalesce(a.cd_pessoa_fisica::text, '') = '') and (a.NM_PESSOA_LISTA = nm_paciente_w))) 
			AND 	a.nr_seq_proc_interno = nr_seq_proc_interno_w 
			AND	a.ie_status_espera = 'A';
			 
			if (qt_reg_w = 0) then 
				SELECT 	coalesce(MAX(1), 0) 
				INTO STRICT	qt_reg_w 
				FROM	AGENDA_LISTA_ESPERA a 
				WHERE 	((a.cd_pessoa_fisica = cd_pessoa_fisica_w) or ((coalesce(a.cd_pessoa_fisica::text, '') = '') and (a.NM_PESSOA_LISTA = nm_paciente_w))) 
				AND 	a.cd_especialidade = cd_especialidade_w 
				AND	a.ie_status_espera = 'A';
			end if;
		ELSE			 
			SELECT 	coalesce(MAX(1), 0) 
			INTO STRICT	qt_reg_w 
			FROM	AGENDA_LISTA_ESPERA a	 
			WHERE 	((a.cd_pessoa_fisica = cd_pessoa_fisica_w) or ((coalesce(a.cd_pessoa_fisica::text, '') = '') and (a.NM_PESSOA_LISTA = nm_paciente_w))) 
			AND	a.ie_status_espera = 'A';
		END IF;	
		 
		IF (qt_reg_w > 0) THEN 
			IF (ie_consiste_dupl_w IN ('BP', 'BPE')) THEN 
				ds_retorno_w := SUBSTR( 
						obter_desc_expressao(771042) || CHR(10) || 
						obter_desc_expressao(771044) 
						,1,254);
			ELSIF (ie_consiste_dupl_w IN ('QP', 'QPE')) THEN 
				ds_retorno_w := SUBSTR( 
						obter_desc_expressao(771044) || CHR(10) || 
						obter_desc_expressao(501410) 
						,1,254);
			END IF;
		END IF;	
	END IF;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
