CREATE OR REPLACE FUNCTION obter_se_estabelecido_usuario (nm_usuario_p text, cd_estabelecimento_verificar_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(1) := 'S';

BEGIN



select  coalesce(max('S'),'N')
into STRICT	ds_retorno_w
from    usuario a
where   a.nm_usuario = nm_usuario_p
and     a.cd_estabelecimento = cd_estabelecimento_verificar_p;


if (ds_retorno_w = 'N') then

	select  coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from    USUARIO_ESTABELECIMENTO a,
		usuario b
	where   a.nm_usuario_param = b.nm_usuario
	and     a.nm_usuario_param = nm_usuario_p
	and     a.cd_estabelecimento = cd_estabelecimento_verificar_p;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
