CREATE OR REPLACE FUNCTION obter_se_estab_autorizacao ( nr_sequencia_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

				
ds_retorno_w			varchar(1) := 'N';
nr_atendimento_w			bigint;
nr_seq_agenda_w                 autorizacao_convenio.nr_seq_agenda%type;
nr_seq_gestao_w			bigint;
nr_seq_age_integ_w		bigint;
nr_seq_agenda_consulta_w        autorizacao_convenio.nr_seq_agenda_consulta%type;
nr_seq_paciente_setor_w		bigint;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
	begin
	select	nr_atendimento,
		nr_seq_agenda,
		nr_seq_gestao,
		nr_seq_age_integ,
		nr_seq_agenda_consulta,
		nr_seq_paciente_setor
	into STRICT	nr_atendimento_w,
		nr_seq_agenda_w,
		nr_seq_gestao_w,
		nr_seq_age_integ_w,
		nr_seq_agenda_consulta_w,
		nr_seq_paciente_setor_w
	from	autorizacao_convenio
	where	nr_sequencia = nr_sequencia_p;
	
	begin
	select	'S'
	into STRICT	ds_retorno_w
	from	atendimento_paciente
	where	nr_atendimento = nr_atendimento_w
	and	cd_estabelecimento = cd_estabelecimento_p  LIMIT 1;
	exception
	when others then
		ds_retorno_w := 'N';
	end;
	
	if (ds_retorno_w = 'N') then
		begin
		begin
		select	'S'
		into STRICT	ds_retorno_w
		from	agenda a,
			agenda_paciente b
		where	a.cd_agenda = b.cd_agenda
		and	b.nr_sequencia = nr_seq_agenda_w
		and	a.cd_estabelecimento = cd_estabelecimento_p  LIMIT 1;
		exception
		when others then
			ds_retorno_w := 'N';
		end;
		
		if (ds_retorno_w = 'N') then
			begin
			begin
			select	'S'
			into STRICT	ds_retorno_w
			from	gestao_vaga
			where	nr_sequencia = nr_seq_gestao_w
			and	cd_estabelecimento = cd_estabelecimento_p  LIMIT 1;
			exception
			when others then
				ds_retorno_w := 'N';
			end;
			
			if (ds_retorno_w = 'N') then
				begin
				begin
				select	'S'
				into STRICT	ds_retorno_w
				from	agenda_integrada
				where	nr_sequencia = nr_seq_age_integ_w
				and	cd_estabelecimento = cd_estabelecimento_p  LIMIT 1;
				exception
				when others then
					ds_retorno_w := 'N';
				end;
				
				if (ds_retorno_w = 'N') then
					begin
					begin
					select	'S'
					into STRICT	ds_retorno_w
					from	agenda a,
						agenda_consulta b
					where	a.cd_agenda = b.cd_agenda
					and	b.nr_sequencia = nr_seq_agenda_consulta_w
					and	a.cd_estabelecimento = cd_estabelecimento_p  LIMIT 1;
					exception
					when others then
						ds_retorno_w := 'N';
					end;
					
					if (ds_retorno_w = 'N') then
						begin
						select	'S'
						into STRICT	ds_retorno_w
						from	paciente_setor
						where	nr_seq_paciente = nr_seq_paciente_setor_w
						and	cd_estabelecimento = cd_estabelecimento_p  LIMIT 1;
						exception
						when others then
							ds_retorno_w := 'N';
						end;
					end if;
					end;
				end if;
				end;
			end if;
			end;
		end if;
		end;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
