SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_estab_integra ( cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_integra_w		varchar(1)	:= 'S';
qt_registros_w		bigint;


BEGIN

if (ie_opcao_p = 'CM') then /*Cadastro de materiais*/
	select	count(*)
	into STRICT	qt_registros_w
	from	estabelecimento_integracao
	where	cd_estabelecimento = cd_estabelecimento_p
	and	coalesce(ie_cadastro_material,'N') = 'N';
elsif (ie_opcao_p = 'UN') then /*Unidade de medida*/
	select	count(*)
	into STRICT	qt_registros_w
	from	estabelecimento_integracao
	where	cd_estabelecimento = cd_estabelecimento_p
	and	coalesce(ie_unidade_medida,'N') = 'N';
end if;

if (qt_registros_w > 0) then
	ie_integra_w := 'N';
else
	ie_integra_w := 'S';
end if;

return	ie_integra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
