CREATE OR REPLACE FUNCTION obter_se_estab_lib_escala (nr_seq_escala_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(1) := 'S';
cd_estabelecimento_w smallint := wheb_usuario_pck.get_cd_estabelecimento;
qt_escala_w bigint;
ie_estab_escala_w varchar(1);


BEGIN
ds_retorno_w := 'S';

ie_estab_escala_w := Obter_param_Usuario(1008, 26, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_estab_escala_w);

if (nr_seq_escala_p > 0 AND ie_estab_escala_w = 'S') then
        select count(1)
        into STRICT qt_escala_w
        from escala a, escala_estab b
        where a.nr_sequencia = b.nr_seq_escala
        and ie_situacao = 'A'
        and b.cd_estabelecimento = cd_estabelecimento_w
        and a.nr_sequencia = nr_seq_escala_p  LIMIT 1;

        if (qt_escala_w >= 1) then
                ds_retorno_w := 'S';
        else
                ds_retorno_w := 'N';
        end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
