CREATE OR REPLACE FUNCTION obter_se_estagio_autor_usuario ( nr_seq_estagio_p bigint, nm_usuario_p text, cd_perfil_p bigint, cd_setor_usuario_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(255);
cont_w			bigint;
cd_estab_lib_w		integer := wheb_usuario_pck.get_cd_estabelecimento;


BEGIN

ie_retorno_w	:= 'S';

select	count(*)
into STRICT	cont_w
from	estagio_autor_usuario
where	nr_seq_estagio	= nr_seq_estagio_p;

if (cont_w > 0) then
	select	count(*)
	into STRICT	cont_w
	from	estagio_autor_usuario
	where	nr_seq_estagio	= nr_seq_estagio_p
	and	coalesce(nm_usuario_lib, coalesce(nm_usuario_p,'X'))		= coalesce(nm_usuario_p,'X')
	and 	coalesce(cd_perfil, coalesce(cd_perfil_p,0))			= coalesce(cd_perfil_p, 0)
	and 	coalesce(cd_setor_atendimento, coalesce(cd_setor_usuario_p, 0))	= coalesce(cd_setor_usuario_p, 0)
	and	coalesce(cd_estab_lib,coalesce(cd_estab_lib_w,0))			= coalesce(cd_estab_lib_w,0);
	if (cont_w = 0) then
		ie_retorno_w	:= 'N';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
