CREATE OR REPLACE FUNCTION obter_se_estipulante_avaliacao ( nr_seq_avaliacao_p bigint, cd_cgc_p text) RETURNS varchar AS $body$
DECLARE

						 
ie_apresenta_w	varchar(1);


BEGIN 
 
select 	coalesce(max('S'),'N') 
into STRICT	ie_apresenta_w 
from 	med_avaliacao_paciente a, 
	pls_segurado b, 
	pls_contrato c 
where 	a.cd_pessoa_fisica	= b.cd_pessoa_fisica 
and 	b.nr_seq_contrato 	= c.nr_sequencia 
and 	c.cd_cgc_estipulante 	= cd_cgc_p 
and	a.nr_sequencia		= nr_seq_avaliacao_p 
and 	((coalesce(b.dt_rescisao::text, '') = '') or (b.dt_rescisao > a.dt_avaliacao));
 
return	ie_apresenta_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
