CREATE OR REPLACE FUNCTION obter_se_estrangeiro_noms (nr_seq_pessoa_endereco_p bigint) RETURNS varchar AS $body$
DECLARE

			
ds_retorno_w	varchar(1);
cd_pais_w		pessoa_endereco_item.ds_valor%type	:= null;	


BEGIN

if (nr_seq_pessoa_endereco_p IS NOT NULL AND nr_seq_pessoa_endereco_p::text <> '') then

	select 	get_info_end_endereco(nr_seq_pessoa_endereco_p, 'PAIS', 'C')
	into STRICT	cd_pais_w
	;

	if (cd_pais_w IS NOT NULL AND cd_pais_w::text <> '') then
		select 	CASE WHEN coalesce(max(b.ie_brasileiro),'S')='S' THEN  'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from 	cat_pais a,
				nacionalidade b
		where	coalesce(b.ie_situacao, 'A') = 'A'
		and		to_char(a.cd_pais) = b.cd_externo
		and 	to_char(a.cd_pais) = cd_pais_w;
	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_estrangeiro_noms (nr_seq_pessoa_endereco_p bigint) FROM PUBLIC;

