CREATE OR REPLACE FUNCTION obter_se_etapa_atual_receb (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


dt_Recebimento_w	timestamp;
ds_retorno_w		varchar(3);
ie_entrou_w			varchar(1);

c01 CURSOR FOR
	SELECT	a.dt_recebimento
	from	fatur_etapa b,
			conta_paciente_etapa a
	where	a.nr_seq_etapa	= b.nr_sequencia
	and		a.nr_interno_conta	= nr_interno_conta_p
	and		coalesce(a.dt_fim_etapa::text, '') = ''
	and		coalesce(b.ie_situacao,'A')= 'A'
	order by a.dt_etapa;


BEGIN

ie_entrou_w	:= 'N';
ds_retorno_w:= 'S';

open	c01;
loop
fetch	c01 into
	 dt_Recebimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	  dt_Recebimento_w 	:= dt_Recebimento_w;
	  ie_entrou_w		:= 'S';
end loop;
close c01;


if (ie_entrou_w = 'S') then

	if (coalesce(dt_recebimento_w::text, '') = '') then
		ds_retorno_w := 'N';
	else
		ds_retorno_w := 'S';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
