CREATE OR REPLACE FUNCTION obter_se_etapa_dependente_npi (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_etapa_processo_w	bigint;
nr_seq_projeto_w		bigint;
retorno_w			varchar(1):='N';
qt_inconsistencia_w		bigint;
nr_seq_tipo_projeto_w	bigint;

C01 CURSOR FOR
	SELECT	c.nr_seq_etapa_processo
	from	proj_cron_etapa a,
		prp_processo_etapa b,
		prp_etapa_dependencia c
	where	1 = 1
	and	a.nr_seq_processo_etapa = b.nr_sequencia
	and	b.nr_seq_etapa_processo = c.nr_seq_etapa_principal
	and	trunc(clock_timestamp()) between trunc(c.dt_inicio_vigencia) and trunc(c.dt_final_vigencia)
	and	a.nr_sequencia = nr_sequencia_p
	and	nr_seq_tipo_projeto = nr_seq_tipo_projeto_w;


BEGIN

retorno_w:= 'N';

select	max(b.nr_seq_proj)
into STRICT	nr_seq_projeto_w
from	proj_cron_etapa a,
	proj_cronograma b
where	a.nr_Seq_cronograma = b.nr_sequencia
and	a.nr_sequencia = nr_sequencia_p;

select	max(nr_seq_tipo_projeto)
into STRICT	nr_seq_tipo_projeto_w
from	proj_projeto
where	nr_sequencia = nr_seq_projeto_w;


open C01;
loop
fetch C01 into
	nr_seq_etapa_processo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_inconsistencia_w
	from	prp_processo a,
		prp_processo_fase b,
		prp_processo_etapa c,
		proj_cron_etapa d
	where	a.nr_Sequencia = b.nr_seq_processo
	and	b.nr_sequencia = c.nr_seq_processo_fase
	and	c.nr_sequencia = d.nr_seq_processo_etapa
	and	c.nr_seq_etapa_processo = nr_seq_etapa_processo_w
	and	a.nr_seq_projeto = nr_seq_projeto_w
	and	d.ie_status_etapa not in (4,1);

	if (qt_inconsistencia_w >0) then
		retorno_w:= 'S';
	end if;

	end;
end loop;
close C01;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
