CREATE OR REPLACE FUNCTION obter_se_etapa_esquema_susp_ap ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_seq_esquema_p bigint) RETURNS varchar AS $body$
DECLARE


ie_etapa_susp_w	varchar(1) := 'N';


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') and (nr_seq_esquema_p IS NOT NULL AND nr_seq_esquema_p::text <> '') then
	begin
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_etapa_susp_w
	from	prescr_solucao_evento
	where	nr_prescricao			= nr_prescricao_p
	and	nr_seq_solucao			= nr_seq_solucao_p
	and	nr_seq_esquema			= nr_seq_esquema_p
	and	ie_alteracao			= 12
	and	coalesce(ie_evento_valido,'S')	= 'S';
	end;
end if;
return ie_etapa_susp_w;
end;
$body$
