SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_etapa_estagio_autor (nr_seq_autor_p bigint, nr_seq_etapa_p bigint) RETURNS varchar AS $body$
DECLARE


ie_visualiza_w          varchar(15) := 'S';
nr_seq_estagio_autor_w  bigint;
qt_existe_w             bigint;


BEGIN
select  nr_seq_estagio
into STRICT    nr_seq_estagio_autor_w
from    autorizacao_convenio
where   nr_sequencia = nr_seq_autor_p;

if (nr_seq_estagio_autor_w IS NOT NULL AND nr_seq_estagio_autor_w::text <> '')then

        select  count(*)
        into STRICT    qt_existe_w
        from    autor_etapa_estagio
        where   nr_seq_etapa   = nr_seq_etapa_p;

        if (qt_existe_w > 0) then

                ie_visualiza_w := 'N';

                select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
                into STRICT    ie_visualiza_w
                from    autorizacao_etapa a,
                        autor_etapa_estagio b
                where   a.nr_sequencia = b.nr_seq_etapa
                and     b.nr_seq_estagio = nr_seq_estagio_autor_w
		and	a.nr_sequencia   = nr_seq_etapa_p;
        else
                ie_visualiza_w := 'S';
        end if;


end if;


return  ie_visualiza_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
