CREATE OR REPLACE FUNCTION obter_se_etapa_motivo_lib ( nr_seq_etapa_p bigint, nr_seq_motivo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';
qt_regra_w	bigint  := 0;


BEGIN


select 	count(*)
into STRICT 	qt_regra_w
from	fatur_etapa_motivo_lib
where	nr_seq_etapa = nr_seq_etapa_p;

if (qt_regra_w > 0) then

	select	count(*)
	into STRICT	qt_regra_w
	from	fatur_etapa_motivo_lib
	where	nr_seq_etapa = nr_seq_etapa_p
	and	nr_seq_motivo = nr_seq_motivo_p;

	if (qt_regra_w > 0) then
		ds_retorno_w := 'S';
	end if;
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
