CREATE OR REPLACE FUNCTION obter_se_etapa_pronta ( nr_sequencia_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w	bigint;
ds_retorno_w	varchar(1) := 'N';


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	far_etapa_producao
where	cd_estabelecimento = cd_estabelecimento_p
and	exists (	SELECT	1
			from	can_ordem_prod
			where	nr_sequencia_p = nr_seq_etapa_prod
			and	coalesce(dt_fim_preparo::text, '') = '');

if (qt_existe_w = 0) then
	ds_retorno_w	:= 'S';
end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
