CREATE OR REPLACE FUNCTION obter_se_etapa_recoleta ( nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE



ie_result_w		varchar(1) := 'N';
ie_etapa_recoleta_w	integer;


BEGIN

select	max(l.ie_status_recoleta)
into STRICT	ie_etapa_recoleta_w
from	lab_parametro l,
	prescr_medica p
where	p.nr_prescricao 	= nr_prescricao_p
and	p.cd_estabelecimento 	= l.cd_estabelecimento;


if (ie_etapa_recoleta_w IS NOT NULL AND ie_etapa_recoleta_w::text <> '') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_result_w
	from	prescr_proc_etapa pe
	where	pe.nr_prescricao 	= nr_prescricao_p
	and	pe.nr_seq_prescricao	= nr_seq_prescr_p
	and	pe.ie_etapa		= ie_etapa_recoleta_w;

end if;

return	ie_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
