CREATE OR REPLACE FUNCTION obter_se_etapa_registrada ( nr_interno_conta_p bigint, nr_seq_etapa_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10);


BEGIN

if (ie_opcao_p	= 'S') then

	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	
	where	exists (	SELECT	1
				from	conta_paciente_etapa
				where	nr_interno_conta	= nr_interno_conta_p
				and	nr_seq_etapa		= nr_seq_etapa_p);

end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
