CREATE OR REPLACE FUNCTION obter_se_etapa_suspensa (nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_etapa_p bigint, nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE


ie_alteracao_w		smallint;
qt_reg_w		smallint := 0;
ie_suspensa_w		varchar(15) := 'N';
dt_suspensao_w		timestamp;

C01 CURSOR FOR
	SELECT	ie_alteracao
	from	prescr_solucao_evento
	where	ie_tipo_solucao = 1
	and	nr_prescricao = nr_prescricao_p
	and	nr_seq_solucao = nr_seq_solucao_p
	and	(((ie_alteracao = 1) or ((ie_alteracao = 2) and (obter_se_motivo_troca_frasco(nr_seq_motivo) = 'S'))) or (ie_alteracao = 12))
	and	ie_evento_valido = 'S';


BEGIN

select	max(dt_suspensao)
into STRICT	dt_suspensao_w
from	prescr_mat_hor
where	nr_sequencia	= nr_seq_horario_p
and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

if (dt_suspensao_w IS NOT NULL AND dt_suspensao_w::text <> '') then
	ie_suspensa_w	:= 'S';
else
	open C01;
	loop
	fetch C01 into
		ie_alteracao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

		if (ie_alteracao_w in (1,2,12)) then
			qt_reg_w := qt_reg_w + 1;
		end if;

		if (qt_reg_w	= nr_etapa_p) and (ie_alteracao_w = 12) then
			ie_suspensa_w	:= 'S';
			exit;
		end if;

	end loop;
	close C01;

end if;

return	ie_suspensa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
