CREATE OR REPLACE FUNCTION obter_se_etiqueta_prescr ( nr_controle_p bigint, nr_seq_etiqueta_p bigint, nr_prescricao_p bigint, cd_barras_etiqueta_p text default null) RETURNS varchar AS $body$
DECLARE


nr_prescricao_w  bigint;
ie_etiqueta_valida_w varchar(1);


BEGIN

select max(nr_prescricao) nr_prescricao
into STRICT nr_prescricao_w
from prescr_proc_controle_etiq a
where (((a.cd_barras IS NOT NULL AND a.cd_barras::text <> '') and cd_barras =  cd_barras_etiqueta_p) or (coalesce(a.cd_barras::text, '') = '' and nr_controle = nr_controle_p and nr_seq_etiqueta = nr_seq_etiqueta_p));

if (nr_prescricao_w = nr_prescricao_p) then
 ie_etiqueta_valida_w := 'S';
else
 ie_etiqueta_valida_w := 'N';
end if;

return ie_etiqueta_valida_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
