CREATE OR REPLACE FUNCTION obter_se_evento_caixa (nr_seq_caixa_p bigint, ie_evento_p text, dt_saldo_caixa_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_permite_evento_w	varchar(1);
ie_regra_w		varchar(1);
nr_seq_caixa_ref_w	bigint;
nr_sequencia_w		bigint;
dt_fechamento_w		timestamp;

c01 CURSOR FOR
SELECT	nr_seq_caixa_ref,
	ie_regra
from	regra_evento_caixa
where	nr_seq_caixa 	= nr_seq_caixa_p
and	ie_evento 	= ie_evento_p;


BEGIN
ie_permite_evento_w 	:= 'S';

OPEN c01;
LOOP
FETCH c01 into
	nr_seq_caixa_ref_w,
	ie_regra_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	caixa_saldo_diario
	where	nr_seq_caixa 		= nr_seq_caixa_ref_w
	and	trunc(dt_saldo,'dd')	= trunc(coalesce(dt_saldo_caixa_p,clock_timestamp()),'dd');

	if (nr_sequencia_w = 0) then
		ie_permite_evento_w := 'N';
	else
		select	dt_fechamento
		into STRICT	dt_fechamento_w
		from	caixa_saldo_diario
		where	nr_seq_caixa 		= nr_seq_caixa_ref_w
		and	trunc(dt_saldo,'dd') 	= trunc(coalesce(dt_saldo_caixa_p,clock_timestamp()),'dd');

		if (ie_regra_w = 'F' and coalesce(dt_fechamento_w::text, '') = '') or (ie_regra_w = 'A' and (dt_fechamento_w IS NOT NULL AND dt_fechamento_w::text <> '')) then
			ie_permite_evento_w := 'N';
		end if;

	end if;
END LOOP;
CLOSE c01;

RETURN 	ie_permite_evento_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
