SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_evolucao_dia (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_evolucao_dia_w	varchar(1) := 'N';
dt_inicial_w		timestamp;
dt_final_w		timestamp;

BEGIN

dt_inicial_w	:= trunc(clock_timestamp());
dt_final_w	:= fim_dia(clock_timestamp());

if (coalesce(nr_atendimento_p,0) > 0)	then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_evolucao_dia_w
	from	evolucao_paciente
	where	nr_atendimento 		= nr_atendimento_p
	and	dt_evolucao 	between dt_inicial_w and dt_final_w
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
end if;

return	ie_evolucao_dia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
