CREATE OR REPLACE FUNCTION obter_se_evolucao_dieta ( nr_atendimento_p text, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_atend_w		varchar(2);
ie_tipo_evolucao_w		varchar(3);
ie_somente_atend_w		varchar(2);
cd_pessoa_fisica_w		varchar(10);


BEGIN

select	coalesce(max(obter_valor_param_usuario(1000, 3, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p)), 0)
into STRICT	ie_tipo_evolucao_w
;

select	coalesce(max(obter_valor_param_usuario(1000, 6, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p)), 'S')
into STRICT	ie_somente_atend_w
;

select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	mapa_dieta
where	nr_atendimento	= nr_atendimento_p;

select	coalesce(max('SA'),'N')
into STRICT	ds_retorno_atend_w
from	evolucao_paciente
where	nr_atendimento 		=	nr_atendimento_p
and	ie_tipo_evolucao	=	ie_tipo_evolucao_w;

if (ie_somente_atend_w = 'N') and (ds_retorno_atend_w = 'N') then

	select	coalesce(max('SP'),'N')
	into STRICT	ds_retorno_atend_w
	from	evolucao_paciente
	where	cd_pessoa_fisica 	=	cd_pessoa_fisica_w
	and	ie_tipo_evolucao	=	ie_tipo_evolucao_w;

end if;

return	ds_retorno_atend_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
