CREATE OR REPLACE FUNCTION obter_se_evol_automatica (cd_item_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint, ie_tipo_item_p text, nr_seq_regra_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w			varchar(1);
									

BEGIN

select	CASE WHEN count(nr_sequencia)=0 THEN 'S'  ELSE 'N' END
into STRICT	ie_permite_w
from	adep_regra_evol_item
where	nr_seq_regra	= nr_seq_regra_p;

if (ie_permite_w	= 'N') then

	if (ie_tipo_item_p	in ('M', 'MAT')) then

		select	coalesce(max('S'),'N')
		into STRICT	ie_permite_w
		from	adep_regra_evol_item
		where	cd_material		= cd_item_p
		and		nr_seq_regra	= nr_seq_regra_p;

	elsif (ie_tipo_item_p	= 'E') then

		select	coalesce(max('S'),'N')
		into STRICT	ie_permite_w
		from	adep_regra_evol_item
		where	nr_seq_procedimento	= cd_item_p
		and		nr_seq_regra		= nr_seq_regra_p;

	elsif (ie_tipo_item_p	= 'R') then

		select	coalesce(max('S'),'N')
		into STRICT	ie_permite_w
		from	adep_regra_evol_item
		where	cd_recomendacao = cd_item_p
		and		nr_seq_regra	= nr_seq_regra_p;

	elsif (ie_tipo_item_p = 'P') then	
	
		select	coalesce(max('S'),'N')
		into STRICT	ie_permite_w
		from	adep_regra_evol_item
		where	cd_procedimento		= cd_item_p
		and		ie_origem_proced 	= ie_origem_proced_p
		and		nr_seq_regra		= nr_seq_regra_p;
		
		if (ie_permite_w	= 'N') then
		
			select	coalesce(max('S'),'N')
			into STRICT	ie_permite_w
			from	adep_regra_evol_item
			where	nr_seq_proc_interno	= nr_seq_proc_interno_p
			and		nr_seq_regra		= nr_seq_regra_p;
		
		end if;
		
	end if;

end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
