SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_exames_aprov_lab (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_proc_lab_w		varchar(1);
qt_proc_prescr_w	bigint;
ds_retorno_w		varchar(1);


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_proc_lab_w
	from	prescr_procedimento
	where	(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
	and		ie_status_atend > 35
	and		nr_prescricao = nr_prescricao_p;

	select	count(*)
	into STRICT	qt_proc_prescr_w
	from	prescr_procedimento
	where	(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
	and		nr_prescricao = nr_prescricao_p;

	if (qt_proc_prescr_w = 0) and (ie_proc_lab_w = 'N') then
		ds_retorno_w := '';
	else
		ds_retorno_w := ie_proc_lab_w;
	end if;

end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
