SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_exame_aprovado_data ( nr_prescricao_p bigint, nr_sequencia_p bigint, dt_aprovacao_p text) RETURNS varchar AS $body$
DECLARE


ie_resultado_w		varchar(1) := 'N';
qtd_w			bigint;


BEGIN

select 	count(*)
into STRICT	qtd_w
from	prescr_procedimento a,
	prescr_medica b
where	a.nr_prescricao = b.nr_prescricao
and	b.nr_prescricao = nr_prescricao_p
and     a.nr_sequencia  = nr_sequencia_p
and	(a.nr_seq_exame IS NOT NULL AND a.nr_seq_exame::text <> '')
and	a.ie_suspenso <> 'S'
and	exists (	SELECT 1
			from 	exame_lab_result_item y,
				exame_lab_resultado x
		    where y.nr_seq_resultado 	= x.nr_seq_resultado
		    and   y.nr_seq_prescr    	= a.nr_sequencia
		    and   x.nr_prescricao    	= b.nr_prescricao
		    and   trunc(y.dt_aprovacao) =   to_date(dt_aprovacao_p,'dd/mm/yyyy'));
if (qtd_w > 0) then
	ie_resultado_w	:= 'S';
end if;

return ie_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
