CREATE OR REPLACE FUNCTION obter_se_exame_baixado ( nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


cd_motivo_baixa_w		smallint;


BEGIN

select	cd_motivo_baixa
into STRICT	cd_motivo_baixa_w
from	prescr_procedimento
where	nr_prescricao = nr_prescricao_p
and		nr_sequencia = nr_seq_prescr_p;

if (cd_motivo_baixa_w = 0) then
	return 'N';
else
	return 'S';
end if;

end;
$body$
LANGUAGE PLPGSQL
