CREATE OR REPLACE FUNCTION obter_se_exame_bloqueado ( nr_sequencia_p bigint, nr_prescricao_p bigint ) RETURNS varchar AS $body$
DECLARE

/**
 Retorna se o exame está bloqueado ou não
*/
ie_restringe_result_w 	 varchar(1) := 'N';
ie_restricao_w		 varchar(1) := 'N';

BEGIN
	Select  coalesce(max(g.ie_restringe_resultado),'N'),
		max(CASE WHEN f.ie_restricao='S' THEN  'S' WHEN f.ie_restricao='I' THEN  'S'  ELSE 'N' END )
	into STRICT	ie_restringe_result_w,
		ie_restricao_w
	from  	prescr_procedimento b,
        	prescr_medica c,
        	exame_lab_resultado e,
        	exame_lab_result_item g,
        	exame_laboratorio f
  	where  	b.nr_prescricao         = c.nr_prescricao
  	and    	b.nr_prescricao         = e.nr_prescricao
        and    	e.nr_seq_resultado      = g.nr_seq_resultado
        and    	g.nr_seq_exame          = f.nr_seq_exame
        and    	b.nr_seq_exame          = f.nr_seq_exame
  	and     g.nr_seq_prescr         = b.nr_sequencia
        and   	b.nr_prescricao         = nr_prescricao_p
        and    	b.nr_sequencia          = nr_sequencia_p;

if (ie_restricao_w <> 'N') then
	ie_restringe_result_w := 'S';
end if;

RETURN ie_restringe_result_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
