CREATE OR REPLACE FUNCTION obter_se_exame_congelacao ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE

ie_congelacao_w	varchar(1);

BEGIN

select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_congelacao_w
from   	prescr_procedimento a,
	exame_laboratorio b
where 	a.nr_prescricao = nr_prescricao_p
and   	b.nr_seq_exame = a.nr_seq_exame
and   	coalesce(ie_congelacao,'N') = 'S';

return	ie_congelacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
