CREATE OR REPLACE FUNCTION obter_se_exame_lab_checado ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

ie_verifica_w	bigint;
ds_retorno_w	varchar(1);

BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
    select	count(*)
    into STRICT	ie_verifica_w
    from	material_exame_lab b,
			exame_lab_rotina a
	where   a.nr_seq_material = b.nr_sequencia
	and     a.nr_sequencia    = nr_sequencia_p
	and		(a.nr_seq_material IS NOT NULL AND a.nr_seq_material::text <> '');
    if (ie_verifica_w = 1) then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;
end if;
return coalesce(ds_retorno_w,'N');
end;
