SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_exame_liberado ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_erro_w				varchar(2000);
nr_seq_proc_interno_w	bigint;
nr_seq_exame_w			bigint;
ie_retorno_w			varchar(1) := 'N';


BEGIN
select	nr_seq_proc_interno,
		nr_seq_exame
into STRICT	nr_seq_proc_interno_w,
		nr_seq_exame_w
from	ageint_regra_exame_lab
where	nr_sequencia = nr_sequencia_p
order by 	nr_sequencia;

if (nr_seq_proc_interno_w > 0) and (coalesce(ie_retorno_w,'N') = 'N') then
	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ie_retorno_w
	from	proc_interno
	where	nr_sequencia = nr_seq_proc_interno_w
	and		coalesce(ie_situacao,'A') = 'A';
end if;

if (nr_seq_exame_w > 0) and (coalesce(ie_retorno_w,'N') = 'N') then
	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ie_retorno_w
	from	exame_laboratorio
	where	nr_seq_exame = nr_seq_exame_w
	and		coalesce(ie_situacao,'A') = 'A';
end if;

if (coalesce(ie_retorno_w,'N') = 'S') then
	ds_erro_w := wheb_mensagem_pck.get_texto(172737, 'EXAME='||coalesce(nr_seq_exame_w,nr_seq_proc_interno_w));
end if;

return ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
