CREATE OR REPLACE FUNCTION obter_se_exame_lib_medico ( cd_pessoa_fisica_p text, nr_seq_exame_rotina_p bigint, nr_seq_proc_rotina_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exibir_w		char(1) := 'S';


BEGIN

select	coalesce(max('N'),'S')
into STRICT	ie_exibir_w
from	regra_exame_rotina_medico where		cd_pessoa_fisica	= cd_pessoa_fisica_p LIMIT 1;

if (ie_exibir_w = 'N') then
	if (nr_seq_exame_rotina_p > 0) then
		select	coalesce(max('N'),'S')
		into STRICT	ie_exibir_w
		from	regra_exame_rotina_medico where		cd_pessoa_fisica	= cd_pessoa_fisica_p
		and		(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '') LIMIT 1;

		if (ie_exibir_w = 'N') then
			select	coalesce(max('S'),'N')
			into STRICT	ie_exibir_w
			from	regra_exame_rotina_medico where		cd_pessoa_fisica	= cd_pessoa_fisica_p
			and		nr_seq_exame	= nr_seq_exame_rotina_p LIMIT 1;
		end if;
	elsif (nr_seq_proc_rotina_p > 0) then
		select	coalesce(max('N'),'S')
		into STRICT	ie_exibir_w
		from	regra_exame_rotina_medico where		cd_pessoa_fisica	= cd_pessoa_fisica_p
		and		(NR_SEQ_PROC_ROTINA IS NOT NULL AND NR_SEQ_PROC_ROTINA::text <> '') LIMIT 1;

		if (ie_exibir_w = 'N') then
			select	coalesce(max('S'),'N')
			into STRICT	ie_exibir_w
			from	regra_exame_rotina_medico where		cd_pessoa_fisica		= cd_pessoa_fisica_p
			and		NR_SEQ_PROC_ROTINA	= nr_seq_proc_rotina_p LIMIT 1;
		end if;
	end if;

end if;

return	ie_exibir_w;

end;
$body$
